import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getFiles } from "../../actions/file";
import FileList from "./fileList/Filelist";
import "./disk.less";
import Popup from "./Popup";
import { setCurrentDir, setPopupFile } from "../../reduser/fileReduser";

const Disk = () => {
  const dispatch = useDispatch();
  const currentDir = useSelector((state) => state.files.currentDir);
  const dirStack = useSelector((state) => state.files.dirStack);

  useEffect(() => {
    dispatch(getFiles(currentDir));
  }, [currentDir]);

  function showPopupCreated() {
    dispatch(setPopupFile(true));
  }

  function backHandler() {
    const backDirId = dirStack.pop();
    dispatch(setCurrentDir(backDirId));
  }

  function fileUploadHendler(e) {
    const files = [...e.target.files];

    files.forEach(file => dispatch());
  }

  return (
    <div className="disk">
      <div className="disk__btns">
        <button onClick={() => backHandler()} className="disk__back">
          Назад
        </button>
        <button className="disk__create" onClick={() => showPopupCreated()}>
          Создать папку
        </button>
        <div className="dick__upload">
          <lable htmlFor="disk__upload-input" className="disk__upload-lable">
            Загрузка файла
          </lable>
          <input
            multiple={true}
            onChange={(e) => fileUploadHendler(e)}
            type="file"
            id="disk__upload-input"
            className="disk__upload-input"
          />
        </div>
      </div>
      <FileList />
      <Popup />
    </div>
  );
};

export default Disk;
